<pre class="lang:default decode:true">/**
 * 
* &lt;p&gt;微信公众号：程序员之路&lt;/p&gt;
* &lt;p&gt;博客：http://www.guangboyuan.cn&lt;/p&gt;
* @ClassName: TurkeyAdapter 
* @Description: 将火鸡适配成鸭子 
* @author RedAnts
* @date 2018年4月8日 上午6:58:08
 */
public class TurkeyAdapter implements Duck {
	Turkey turkey;

	public TurkeyAdapter(Turkey turkey) {
		this.turkey = turkey;
	}

	@Override
	public void quack() {
		turkey.gobble();
	}

	@Override
	public void fly() {
		for (int i = 0; i &lt; 5; i++) {
			turkey.fly();
		}
	}

}</pre>
<pre class="lang:default decode:true ">import com.chengxuyuanzhilu.now5.impl.MallardDuck;
